package com.txdot.isd.rts.server.v21.vehicleinfo.data;/* * WsVehicleInfoV21DataReq.java * * (c) Texas Department of Transportation 2008 * --------------------------------------------------------------------- * Change History: * Name			Date		Description * ------------	-----------	-------------------------------------------- * Ray Rowehl	01/14/2008	New class * 							defect 9502 Ver 3_Amigos_PH_A * --------------------------------------------------------------------- *//** * Contains Vehicle Information Request Data from V21 to lookup a  * Vehicle Record. * * @version	3_Amigos_PH_A	01/14/2008 * @author	Ray Rowehl * <br>Creation Date:		01/14/2008 11:11:55 *//* &WsVehicleInfoV21DataReq& */public class WsVehicleInfoV21DataReq{	/**	 * Doument Number.	 * 	 * <p>If provided, this overrides all other parameters.	 *//* &WsVehicleInfoV21DataReq'csDocumentNumberReq& */	private String csDocumentNumberReq = "";		/**	 * Last four charaters of the VIN.	 * 	 * <p>Used to confirm the plate number returned.	 *//* &WsVehicleInfoV21DataReq'csLastFourOfVin& */	private String csLastFourOfVin = "";		/**	 * Plate Number.	 * 	 * <p>This is the most likely path to getting a record.	 * Must be paired with the last for of the VIN to find a match.	 *//* &WsVehicleInfoV21DataReq'csRegPlateReq& */	private String csRegPlateReq = "";		/**	 * Vehicle Identification Number.	 * 	 * <p>This is used before plate number if both are provided.	 *//* &WsVehicleInfoV21DataReq'csVINReq& */	private String csVINReq = "";		/**	 * Used to determine if we want a VINA response when looking up 	 * by VIN.	 *//* &WsVehicleInfoV21DataReq'cbVinaLookupNeeded& */	private boolean cbVinaLookupNeeded = true;			/**	 * WsVehicleInfoV21DataReq.java Constructor	 *//* &WsVehicleInfoV21DataReq.WsVehicleInfoV21DataReq& */	public WsVehicleInfoV21DataReq()	{		super();	}	/**	 * The requested Document Number	 * 	 * @return String	 *//* &WsVehicleInfoV21DataReq.getDocumentNumberReq& */	public String getDocumentNumberReq()	{		return csDocumentNumberReq;	}	/**	 * The requested Last Four characters of the VIN.	 * 	 * @return String	 *//* &WsVehicleInfoV21DataReq.getLastFourOfVin& */	public String getLastFourOfVin()	{		return csLastFourOfVin;	}	/**	 * The requested Plate Number.	 * 	 * @return String	 *//* &WsVehicleInfoV21DataReq.getRegPlateReq& */	public String getRegPlateReq()	{		return csRegPlateReq;	}	/**	 * The requested VIN.	 * 	 * @return String	 *//* &WsVehicleInfoV21DataReq.getVINReq& */	public String getVINReq()	{		return csVINReq;	}		/**	 * Returns VINA Lookup Needed Indicator.	 * 	 * @return boolean	 *//* &WsVehicleInfoV21DataReq.isVinaLookupNeeded& */	public boolean isVinaLookupNeeded()	{		return cbVinaLookupNeeded;	}	/**	 * Set the requested Document Number	 * 	 * @param asDocumentNumberReq	 *//* &WsVehicleInfoV21DataReq.setDocumentNumber& */	public void setDocumentNumber(String asDocumentNumberReq)	{		if (asDocumentNumberReq != null)		{			csDocumentNumberReq = asDocumentNumberReq;		}		else		{			csDocumentNumberReq = "";		}	}	/**	 * Set the requested Last Four characters of the VIN.	 * 	 * @param asLastFourOfVin	 *//* &WsVehicleInfoV21DataReq.setLastFourOfVin& */	public void setLastFourOfVin(String asLastFourOfVin)	{		if (asLastFourOfVin != null)		{			csLastFourOfVin = asLastFourOfVin;		}		else		{			csLastFourOfVin = "";		}	}	/**	 * Set the requested Plate Number.	 * 	 * @param asRegPlateReq	 *//* &WsVehicleInfoV21DataReq.setRegPlate& */	public void setRegPlate(String asRegPlateReq)	{		if (asRegPlateReq != null)		{			csRegPlateReq = asRegPlateReq;		}		else		{			csRegPlateReq = "";		}	}	/**	 * Set the requested VIN.	 * 	 * @param asVINReq	 *//* &WsVehicleInfoV21DataReq.setVIN& */	public void setVIN(String asVINReq)	{		if (asVINReq != null)		{			csVINReq = asVINReq;		}		else		{			csVINReq = "";		}	}		/**	 * Set the VINA Lookup boolean.	 * 	 * @param abVinaLookupNeeded	 *//* &WsVehicleInfoV21DataReq.setVinaLookupNeeded& */	public void setVinaLookupNeeded(boolean abVinaLookupNeeded)	{		cbVinaLookupNeeded = abVinaLookupNeeded;	}}/* #WsVehicleInfoV21DataReq# */