package com.txdot.isd.rts.services.data;import java.io.Serializable;import com.txdot.isd.rts.services.util.RTSDate;import com.txdot.isd.rts.services.util.UtilityMethods;/* * FraudReportUIData.java * * (c) Texas Department of Transportation 2011 * --------------------------------------------------------------------- * Change History: * Name			Date		Description * ------------	-----------	-------------------------------------------- * K Harrell	06/06/2011	Created * 							defect 10900 Ver 6.8.0  * --------------------------------------------------------------------- *//** * This Data class contains attributes and get and set methods for  * FraudUIData * * @version	6.8.0			06/06/2011 * @author	Kathy Harrell * <br>Creation Date:		06/06/2011	17:17:17  *//* &FraudUIData& */public class FraudUIData implements Serializable{/* &FraudUIData'caBeginDate& */	private RTSDate caBeginDate;/* &FraudUIData'caEndDate& */	private RTSDate caEndDate;/* &FraudUIData'caFraudTypeData& */	private FraudStateData caFraudTypeData;/* &FraudUIData'ciTransType& */	private int ciTransType;/* &FraudUIData'csAddlSearch& */	private String csAddlSearch;/* &FraudUIData'csSearchKey& */	private String csSearchKey;/* &FraudUIData'ADD& */	public final static int ADD = 1;/* &FraudUIData'ALL& */	public final static int ALL = 2;/* &FraudUIData'DELETE& */	public final static int DELETE = 0;/* &FraudUIData'serialVersionUID& */	static final long serialVersionUID = 6657558705784061138L;	/**	 * FraudReportUIData.java Constructor	 * 	 *//* &FraudUIData.FraudUIData& */	public FraudUIData()	{		super();	}	/**	 * Return value of csAddlSearch	 * 	 * @return String 	 *//* &FraudUIData.getAddlSearch& */	public String getAddlSearch()	{		return csAddlSearch;	}	/**	 * Return value of caBeginDate	 * 	 * @return RTSDate	 *//* &FraudUIData.getBeginDate& */	public RTSDate getBeginDate()	{		return caBeginDate;	}	/**	 * Return value of caEndDate	 * 	 * @return RTSDate	 *//* &FraudUIData.getEndDate& */	public RTSDate getEndDate()	{		return caEndDate;	}	/**	 * Return value of caFraudTypeData	 * 	 * @return FraudStateData	 *//* &FraudUIData.getFraudTypeData& */	public FraudStateData getFraudTypeData()	{		return caFraudTypeData;	}	/**	 * Return value of csSearchKey	 * 	 * @return String	 *//* &FraudUIData.getSearchKey& */	public String getSearchKey()	{		return csSearchKey;	}	/**	 * Return value of ciTransType	 * 	 * @return int	 *//* &FraudUIData.getTransType& */	public int getTransType()	{		return ciTransType;	}		/**	 * Return Where Clause Data 	 * 	 *//* &FraudUIData.getWhereClauseData& */	public String getWhereClauseData()	{		String lsWhereClause =			"TransAMDate between "				+ caBeginDate.getAMDate()				+ " and "				+ caEndDate.getAMDate();		if (ciTransType != ALL)		{			lsWhereClause =				lsWhereClause + " and AddFraudIndi = " + ciTransType;		}		if (!caFraudTypeData.isAllTypes())		{			lsWhereClause =				lsWhereClause					+ " and A.FraudCd in ("					+ caFraudTypeData.getInClauseParms()					+ ") ";		}		if (!UtilityMethods.isEmpty(csAddlSearch))		{			lsWhereClause =				lsWhereClause					+ " and "					+ csAddlSearch					+ " = '"					+ csSearchKey.trim()					+ "'";		}		return lsWhereClause;	}	/**	 * Set value of csAddlSearch	 * 	 * @param asAddlSearch	 *//* &FraudUIData.setAddlSearch& */	public void setAddlSearch(String asAddlSearch)	{		csAddlSearch = asAddlSearch;	}	/**	 * Set value of caBeginDate	 * 	 * @param aaBeginDate	 *//* &FraudUIData.setBeginDate& */	public void setBeginDate(RTSDate aaBeginDate)	{		caBeginDate = aaBeginDate;	}	/**	 * Set value of caEndDate	 * 	 * @param aaEndDate	 *//* &FraudUIData.setEndDate& */	public void setEndDate(RTSDate aaEndDate)	{		caEndDate = aaEndDate;	}	/**	 * Set value of caFraudTypeData	 * 	 * @param aaFraudTypeData	 *//* &FraudUIData.setFraudTypeData& */	public void setFraudTypeData(FraudStateData aaFraudTypeData)	{		caFraudTypeData = aaFraudTypeData;	}	/**	 * Set value of csSearchKey	 * 	 * @param asSearchKey	 *//* &FraudUIData.setSearchKey& */	public void setSearchKey(String asSearchKey)	{		csSearchKey = asSearchKey;	}	/**	 * Set value of ciTransType	 * 	 * @param aiTransType	 *//* &FraudUIData.setTransType& */	public void setTransType(int aiTransType)	{		ciTransType = aiTransType;	}}/* #FraudUIData# */