package com.txdot.isd.rts.services.util;/* * FtpFile.java * * (c) Texas Department of Transportation 2009 * --------------------------------------------------------------------- * Change History: * Name			Date		Description * ------------	-----------	-------------------------------------------- * B Brown		03/24/2009	Created Class. * 							defect 9986 Ver Defect_POS_E * --------------------------------------------------------------------- *//** * This class is used to hold FTP properties used to connect * to a remote computer and transfer files.  * * @version	Defect_POS_E	03/24/2009 * @author	Bob Brown * <br>Creation Date:		03/19/2009 13:48:30 *//* &FtpFile& */public class FtpFile	{/* &FtpFile'caFTPFile& */		private final String caFTPFile;/* &FtpFile'caFTPHost& */		private final String caFTPHost;/* &FtpFile'caFTPPassword& */		private final String caFTPPassword;/* &FtpFile'caFTPUser& */		private final String caFTPUser;/* &FtpFile'caLocalFile& */		private final String caLocalFile;		/**	 * FtpFile represents a file transfer object.	 *	 * @param asFTPHost String	 * @param asFTPUser String	 * @param asFTPPassword String	 * @param asFTPFile String	 * @param asLocalFile String	 *//* &FtpFile.FtpFile& */	public FtpFile(		String asFTPHost,		String asFTPUser,		String asFTPPassword,		String asFTPFile,		String asLocalFile)	{		super();		this.caFTPHost = asFTPHost;		this.caFTPUser = asFTPUser;		this.caFTPPassword = asFTPPassword;		this.caFTPFile = asFTPFile;		this.caLocalFile = asLocalFile;	}	/**	 * @return Returns the caFTPFile.	 *//* &FtpFile.getFtpFile& */	public String getFtpFile()	{		return this.caFTPFile;	}	/**	 * @return Returns the caFTPHost.	 *//* &FtpFile.getFtpHost& */	public String getFtpHost()	{		return this.caFTPHost;	}	/**	 * @return Returns the caFTPPassword.	 *//* &FtpFile.getFtpPassword& */	public String getFtpPassword()	{		return this.caFTPPassword;	}	/**	 * @return Returns the caFTPUser.	 *//* &FtpFile.getFtpUser& */	public String getFtpUser()	{		return this.caFTPUser;	}	/**	 * @return Returns the caLocalFile.	 *//* &FtpFile.getLocalFile& */	public String getLocalFile()	{		return this.caLocalFile;	}}/* #FtpFile# */