package com.txdot.isd.rts.services.util;import java.util.*;import java.io.*;/* &StreamGobbler& */class StreamGobbler extends Thread{/* &StreamGobbler'is& */	InputStream is;/* &StreamGobbler'type& */	String type;    /* &StreamGobbler.StreamGobbler& */	StreamGobbler(InputStream is, String type)	{		this.is = is;		this.type = type;	}    /* &StreamGobbler.run& */	public void run()	{		try		{			InputStreamReader isr = new InputStreamReader(is);			BufferedReader br = new BufferedReader(isr);			String line=null;			while ( (line = br.readLine()) != null)				System.out.println(type + ">" + line);    			} catch (IOException ioe)			  {				ioe.printStackTrace();  			  }	}}/* #StreamGobbler# */